mapper.select:
SELECT a.oId,a.oPrice,a.oDate,a.oOver,b.uAddress,b.uName,b.uId,b.uTel,c.number,d.bName,d.bPrice,d.bId
FROM order1 a INNER JOIN  user b ON a.uId=b.uId INNER JOIN book_order c on c.oId=a.oId  INNER JOIN book d on d.bId=c.bId
where a.uId=#{paramEntity.uId}
order by a.oId
limit #{start},#{rows}

SELECT count(1)
FROM order1 a INNER JOIN  user b ON a.uId=b.uId INNER JOIN book_order c on c.oId=a.oId  INNER JOIN book d on d.bId=c.bId
where a.uId=#{paramEntity.uId}
